function formCheck(e){for(var r=0;r<e.elements.length;r++){var n=e.elements[r];if("required"==n.required&&!isFilled(n))return alert("请填上"+n.name+"."),!1;if("email"==n.name&&isntEmail(n))return alert("请写上有效的邮箱地址"),!1}return!0}function isFilled(e){if(0==e.value.replace(""," ").length)return!1;var r=e.placeholder||e.getAttribute("placeholder");return e.value!=r}function isntEmail(e){var r=e.value,n=r.indexOf("@"),l=r.indexOf(".");return n<1||l<n+2||l+2>=r.length}